<script setup>

import { computed } from "vue"

const props = defineProps({
  name: {
    type: String,
    required: true
  },
  size: {
    type: Number,
    default: 22
  },
  color: {
    type: String,
    default: ''
  }
})

const iconCls = computed(()=> {
  return `svg-icon-${props.name}`
})

const svgStyle = computed(()=> {
  let style = `height: ${props.size}px;width: ${props.size}px;`
  if (props.color) {
    style += `color: ${props.color};`
  }
  return style
})

</script>

<template>
  <i class="svg-icon" :class="iconCls" :style="svgStyle"></i>
</template>

